"use strict";

(function () {
  var bonus = document.querySelector(".bonus-rules-link");
  var bonusSecond = document.querySelector(".win-second__link");
  var close = document.querySelector(".popup__window-close");
  var popup = document.querySelector(".popup");
  var overflow = document.querySelector("body");
  var pulseBtn = document.querySelector(".banner__button");
  var winclose = document.querySelector(".win__button");
  var wincloseSecond = document.querySelector(".win-second__button");
  var win = document.querySelector(".win");
  var winSecond = document.querySelector(".win-second");
  var wheel = document.querySelector(".banner__wheel_reel");
  var tries = document.querySelector(".tries");
  var triesCounter = 0;
  var totalTriesCounter = 2;
  bonus.addEventListener("click", function () {
    popup.style.display = "flex";
    overflow.style.overflow = "hidden";
  });
  close.addEventListener("click", function () {
    popup.style.display = "none";
    overflow.style.overflow = "visible";
  });
  pulseBtn.addEventListener('click', function () {
    if (triesCounter === 0) {
      firstRotation();
    } else {
      secondRotation();
    }
  });

  function changeTriesCounter() {
    var number = totalTriesCounter - triesCounter;

    if (number === 2) {
      tries.innerText = "2 спроби";
    } else if (number === 1) {
      tries.innerText = "1 спроба";
    } else {
      tries.innerText = "0 спроб";
    }
  }

  changeTriesCounter();

  function firstRotation() {
    wheel.classList.add("spin");
    setTimeout(function () {
      win.classList.add("block");
      overflow.style.overflow = "hidden";
    }, 3300);
    triesCounter++;
    changeTriesCounter();
  }

  function secondRotation() {
    wheel.classList.add("spin-second");
    setTimeout(function () {
      winSecond.classList.add("block");
      overflow.style.overflow = "hidden";
    }, 3300);
    triesCounter++;
    changeTriesCounter();
  }

  winclose.addEventListener("click", function () {
    win.classList.remove("block");
    overflow.style.overflow = "visible";
  });
  wincloseSecond.addEventListener("click", function () {
    winSecond.classList.remove("block");
    overflow.style.overflow = "visible";
  });
  bonusSecond.addEventListener("click", function () {
    winSecond.classList.remove("block");
    popup.style.display = "flex";
    overflow.style.overflow = "hidden";
  });
})();
"use strict";
